import Head from "next/head";
import { PostData, loadBlogPosts } from "../loader";
import { PostCard } from "../components/PostCard";
import { generateRSS } from "../rssUtil";
import { globals } from "../globals";

const sectionStyle = {
  width: "100%",
  padding: "100px 3vw",
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
} as const;

const Home = (props: { introduction: string; posts: PostData[] }) => {
  return (
    <div
      style={{
        width: "100%",
      }}
    >
      <Head>
        <title>dev.lwlx.xyz | Cybersecurity & Internet Freedom</title>
        <meta
          name="description"
          content="dev.lwlx.xyz publishes articles and research about Cybersecurity, DevOps, Development or just general IT nonsense. "
        />
        <meta
          property="og:image"
          content={`https://dev.lwlx.xyz/lwlzcolors-lawlez-sm.jpg`}
        />
        <link rel="icon" href="/favicon.ico" />
      </Head>

      <div style={sectionStyle}>
        <h2
          style={{
            margin: "4px 0px",
            fontSize: "34pt",
          }}
        >
          newest posts
        </h2>
        <div
          style={{
            display: "grid",
            gridTemplateColumns: `repeat(auto-fit, minmax(300px, 1fr))`,
            gridRowGap: "8px",
            gridColumnGap: "8px",
            width: "100%",
            padding: "0px 7vw",
          }}
        >
          {props.posts.map((post, key) => {
            let isTagged = false;
            if (post && post.tags) {
              post.tags.forEach((tag) => {
                if (tag) {
                  isTagged = true;
                }
              });
              if (
                (isTagged && key === props.posts.length - 1) ||
                (isTagged && key === props.posts.length - 2) ||
                (isTagged && key === props.posts.length - 3) ||
                (isTagged && key === props.posts.length - 4)
              ) {
                return <PostCard key={key} post={post} />;
              }
              return;
            }
            return `the post ${post.title} has no tags defined`;
          })}
        </div>
      </div>

      <div style={sectionStyle}>
        <h2
          style={{
            margin: "4px 0px",
            fontSize: "34pt",
          }}
        >
          CyberSecurity and Information Security posts
        </h2>
        <div
          style={{
            display: "grid",
            gridTemplateColumns: `repeat(auto-fit, minmax(300px, 1fr))`,
            gridRowGap: "8px",
            gridColumnGap: "8px",
            width: "100%",
            padding: "0px 7vw",
          }}
        >
          {props.posts.map((post, key) => {
            let isCyberSecPost = false;
            if (post && post.tags) {
              post.tags.forEach((tag) => {
                if (
                  tag === "security" ||
                  tag === "cybersecurity" ||
                  tag === "appsec"
                ) {
                  isCyberSecPost = true;
                }
              });
              if (isCyberSecPost) {
                return <PostCard key={key} post={post} />;
              }
              return;
            }
            return `the post ${post.title} has no tags defined`;
          })}
        </div>
      </div>

      <div style={sectionStyle}>
        <h2
          style={{
            margin: "4px 0px",
            fontSize: "34pt",
          }}
        >
          Devtools and Js Resources
        </h2>
        <div
          style={{
            display: "grid",
            gridTemplateColumns: `repeat(auto-fit, minmax(300px, 1fr))`,
            gridRowGap: "8px",
            gridColumnGap: "8px",
            width: "100%",
            padding: "0px 7vw",
          }}
        >
          {props.posts.map((post, key) => {
            let isDevPost = false;
            if (post && post.tags) {
              post.tags.forEach((tag) => {
                if (
                  tag === "javascript" ||
                  tag === "devtools" ||
                  tag === "nodejs"
                ) {
                  isDevPost = true;
                }
              });
              if (isDevPost) {
                return <PostCard key={key} post={post} />;
              }
              return;
            }
            return `the post ${post.title} has no tags defined`;
          })}
        </div>
      </div>

      <div style={{ ...sectionStyle }}>
        <h2
          style={{
            textAlign: "center",
            fontSize: "34pt",
          }}
        >
          Open Source Security & Dev Blog
        </h2>
        <p
          style={{
            textAlign: "center",
            maxWidth: "960px",
            margin: "auto",
            lineHeight: 1.7,
          }}
        >
          dev.lwlx.xyz is a blog focused on Cybersecurity and the occasional
          code project.
        </p>
        <br />
        <a href="https://twitter.com/0x0000005" style={{ cursor: "pointer" }}>
          <button
            style={{
              padding: "10px 30px",
              backgroundColor: globals.secondaryColor,
              color: "white",
              fontSize: "14pt",
              border: "none",
              borderRadius: "8px",
              cursor: "pointer",
            }}
          >
            @0x0000005 on twitter
          </button>
        </a>
      </div>

      <div style={sectionStyle}>
        <h2
          style={{
            margin: "4px 0px",
            fontSize: "22pt",
            paddingBottom: "30px",
          }}
        >
          this is still a work in progress . . .
        </h2>
        <a
          href="https://github.com/Lawlez/dev.lwlx.xyz"
          style={{ cursor: "pointer" }}
        >
          <button
            style={{
              padding: "10px 30px",
              backgroundColor: globals.primaryColor,
              color: "white",
              fontSize: "14pt",
              border: "none",
              borderRadius: "8px",
              cursor: "pointer",
            }}
          >
            check dev.lwlx.xyz on GitHub
          </button>
        </a>
      </div>
    </div>
  );
};

export default Home;

export const getStaticProps = async () => {
  //const introduction = await loadMarkdownFile('introduction.md');
  const posts = await loadBlogPosts();

  // comment out to turn off RSS generation during build step.
  await generateRSS(posts);

  const props = {
    introduction: "empty for now :)", //introduction.contents,
    posts,
  };

  return { props };
};
